import React,{useState} from 'react'
import {Link} from 'react-router-dom';
import {connect} from 'react-redux';
import{buyStock,sellStock,viewStock,removeStock} from '../redux/actions.js'

 function PortfolioComponent({portfolioElement,buyStock,sellStock,removeStock,viewStock}) {
   
    const[inputQuantity, setInputQuantity] =useState(0);
    const stockName=portfolioElement.longName;
    const stockPrice=portfolioElement.regularMarketPrice;
    const quantity=portfolioElement.quantity;
    
    const change=portfolioElement.regularMarketChange;
    const priceColor=change<0?'product-price-red':'product-price-green';


    return (
        <div className="product-container">
        <h3 className="product-heading">{stockName} </h3>
        <h3 className={priceColor}>{stockPrice}</h3>
        <span className="quantity-display">Stock qty{' : '}{quantity}</span>
        <div className="btn-container">
            <input type="number" className="quantitty" placeholder="0" min={0} onChange={(e)=>setInputQuantity(e.target.value)}></input>
            <button onClick={ ()=>{
                                    buyStock(portfolioElement.symbol,inputQuantity)
                                   }} className="btn-buy">Buy</button>
            <button onClick={()=>{sellStock(portfolioElement.symbol,inputQuantity)
                                   removeStock()}} className="btn-sell">Sell</button>
        </div>
        
        <Link onClick={()=>viewStock(portfolioElement.symbol)} to="/stock">
        <button className="view">Details</button></Link>
    </div>
    )
}
const mapDispatchToProps=dispatch=>{
    return{
        buyStock:(id,qty)=>{
            dispatch(buyStock(id,qty))
        },
        sellStock:(id,qty)=>{
            dispatch(sellStock(id,qty))
        },
        removeStock:()=>{
            dispatch(removeStock())
        },
        viewStock:(id)=>{
            dispatch(viewStock(id))
        }
    }

}
export default connect(null,mapDispatchToProps)(PortfolioComponent);
