import * as actionTypes from './types.js';
import dummyData from './dummyData.js';
const initialState={
    products:dummyData,
    portfolio:[],
    currentItem:null,
    investmentValue:0,
    currentValue:0,
}

const shopReducer=(state=initialState,action)=>{
    switch(action.type)
    {
        case actionTypes.BUY:
            const item=state.products.find(prod=>prod.symbol===action.payload.id)
            const inPortfolio=state.portfolio.find(item=>item.symbol===action.payload.id?true:false);
        return{
                 ...state,
                 portfolio:inPortfolio?
                 state.portfolio.map(
                     (i)=>
                     i.symbol===action.payload.id?
                     {...i,quantity:i.quantity+action.payload.qty}:i):
                     [...state.portfolio,{...item,quantity:action.payload.qty}]
        };
        case actionTypes.SELL:return{
            ...state,
                 portfolio:inPortfolio?
                 state.portfolio.map(
                     (i)=>
                     i.symbol===action.payload.id&&
                     i.quantity>=action.payload.qty?
                     {...i,quantity:i.quantity-action.payload.qty}
                     :i
                     )
                     :state.portfolio
        }
        case actionTypes.VIEW:return{
               currentItem:action.payload.id
        }
        default:return state;
    }
};
export default shopReducer;
