import React,{useState} from 'react'
import {Link} from 'react-router-dom';
import {connect} from 'react-redux';
import{buyStock,sellStock} from '../redux/actions.js'
 function Product({product}) {
    const stockSymbol=product.longName;
    const stockPrice=product.regularMarketPrice;
 var inputQuantity=0;
const inputHandler=(e)=>{
        inputQuantity=e.target.value;
        console.log()
        console.log(inputQuantity);
}

    const buyClickHandler=()=>{
        buyStock(inputQuantity)
    }
    const sellClickHandler=()=>{return 0}
    return (
        <div className="product-container">
            <h3 className="product-heading">{stockSymbol} </h3>
            <h3 className="product-price">{stockPrice+' '+inputQuantity}</h3>
            <div className="btn-container">
                <input type="number" className="quantitty" placeholder="0" min={0} onChange={inputHandler}></input>
                <button onClick={buyClickHandler} className="btn-buy">Buy</button>
                <button onClick={sellClickHandler} className="btn-sell">Sell</button>
            </div>

            <Link to="/product/:id">
            <button className="view">Details</button></Link>
        </div>
    )
}
const mapDispatchToProps=dispatch=>{
    return {
          buyStock:(id,qty)=>
          {
              dispatch(buyStock(id,qty))
          }
    }

}




export default connect(null,mapDispatchToProps)(Product);
