import React from 'react'

export default function PortfolioComponent() {
    return (
        <div className="product-container">
        <h3 className="product-heading">{stockName} </h3>
        <h3 className="product-price">{stockPrice}</h3>
        <div className="btn-container">
            <input type="number" className="quantitty" placeholder="0" min={0} onChange={(e)=>setInputQuantity(e.target.value)}></input>
            <button onClick={ ()=>{
             buyStock(product.symbol,inputQuantity)
}} className="btn-buy">Buy</button>
            <button onClick={()=>{sellStock(product.symbol,inputQuantity)}} className="btn-sell">Sell</button>
        </div>

        <Link to="/product/:id">
        <button className="view">Details</button></Link>
    </div>
    )
}
