import React from 'react'
import { connect } from 'react-redux';
import PortfolioComponent from './PortfolioComponent'

function Portfolio({portfolio,invested}) {

 

    return (
        <div>
              <div className="total-invested">Total Investment={'₹ '}{invested}</div>
        {

             portfolio.map(portfolioElement=>{return (<PortfolioComponent key={portfolioElement.symbol} portfolioElement={portfolioElement}/>)})
        }
       
           
        
         
      </div>
        )
    
}
const mapStateToProps=state=>{
    return{portfolio:state.trade.portfolio,
        invested:state.trade.invested
    }
    
}
export default connect(mapStateToProps)(Portfolio);
