import React,{useState,useEffect} from 'react'
import {Link} from 'react-router-dom';
import {connect} from 'react-redux';
import{buyStock,sellStock,viewStock} from '../redux/actions.js'

export default function PortfolioComponent({product}) {
    const[inputQuantity, setInputQuantity] =useState(0);
    const stockName=product.longName;
    const stockPrice=product.regularMarketPrice;
    const quantity=product.quantity;
    return (
        <div className="product-container">
        <h3 className="product-heading">{stockName} </h3>
        <h3 className="product-price">{stockPrice}</h3>
        <span className="quantity-display">Stock qty{' : '}{quantity}</span>
        <div className="btn-container">
            <input type="number" className="quantitty" placeholder="0" min={0} onChange={(e)=>setInputQuantity(e.target.value)}></input>
            <button onClick={ ()=>{
                                    buyStock(product.symbol,inputQuantity)
                                   }} className="btn-buy">Buy</button>
            <button onClick={()=>{sellStock(product.symbol,inputQuantity)}} className="btn-sell">Sell</button>
        </div>
        
        <Link to="/product/:id">
        <button className="view">Details</button></Link>
    </div>
    )
}
